import React from 'react';
import millify from 'millify';
import { Typography, Row, Col, Statistic } from 'antd';
import { Link } from 'react-router-dom';
import { useGetCryptosQuery } from '../services/cryptoApi';
import { Cryptocurrencies, News } from '../components';

const { Title } = Typography;

const Homepage = () => {
  const { data, isFetching } = useGetCryptosQuery(10);
  const globalStats = data?.data?.stats;
  console.log(data);
  if (isFetching) return 'Loading...';

  return (
    <>
      <Title level={2} className="heading">Global Crypto Stats</Title>
      <Row>
        <Col span={12}>
          <Statistic title="Total Cryptocurrencies" value={globalStats?.total || 'N/A'} />
        </Col>
        <Col span={12}>
          <Statistic title="Total Exchanges" value={globalStats ? millify(globalStats.totalExchanges) : 'N/A'} />
        </Col>
        <Col span={12}>
          <Statistic title="Total Market Cap:" value={globalStats ? millify(globalStats.totalMarketCap) : 'N/A'} />
        </Col>
        <Col span={12}>
          <Statistic title="Total 24h Volume" value={globalStats ? millify(globalStats.total24hVolume) : 'N/A'} />
        </Col>
        <Col span={12}>
          <Statistic title="Total Markets" value={globalStats ? millify(globalStats.totalMarkets) : 'N/A'} />
        </Col>
      </Row>
      <div className='home-heading-container'>
        <Title level={2} className='home-title'>Top 10 Cryptocurrencies in the world</Title>
        <Title level={3} className='show-more'><Link to='/cryptocurrencies'>Show more</Link></Title>
      </div>
      <Cryptocurrencies simplified />
      <div className='home-heading-container'>
        <Title level={2} className='home-title'>Latest Crypto News</Title>
        <Title level={3} className='show-more'><Link to='/news'>Show more</Link></Title>
      </div>
      <News simplified />
    </>
  );
};

export default Homepage;
